import React, { useEffect, useState } from "react";
import axios from "axios";
import ChatWindow from "./components/ChatWindow";
import "./App.css";
import { io } from "socket.io-client";

// âœ… Change this to your deployed backend URL
const API_URL = "https://chat-system-jyjhtbn8w-madhavichute526-gmailcoms-projects.vercel.app";

const socket = io(API_URL);

function App() {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [newMessage, setNewMessage] = useState("");
  const [typing, setTyping] = useState(false);

  useEffect(() => {
    fetchMessages();

    socket.on("new_message", (msg) => {
      setChats((prevChats) => {
        const updated = [...prevChats];
        const existing = updated.find((c) => c.wa_id === msg.wa_id);

        if (existing) {
          const alreadyExists = existing.messages.some(
            (m) => m.meta_msg_id === msg.meta_msg_id
          );

          if (!alreadyExists) {
            existing.messages.push(msg);
          }
        } else {
          updated.push({
            wa_id: msg.wa_id,
            name: msg.name,
            number: msg.number,
            messages: [msg],
          });
        }

        return [...updated];
      });
    });

    socket.on("status_updated", (updatedMsg) => {
      setChats((prevChats) =>
        prevChats.map((chat) => {
          if (chat.wa_id === updatedMsg.wa_id) {
            const newMessages = chat.messages.map((msg) =>
              msg.meta_msg_id === updatedMsg.meta_msg_id
                ? { ...msg, status: updatedMsg.status }
                : msg
            );
            return { ...chat, messages: newMessages };
          }
          return chat;
        })
      );
    });

    socket.on("user_typing", (wa_id) => {
      if (selectedChat?.wa_id === wa_id) {
        setTyping(true);
        setTimeout(() => setTyping(false), 1500);
      }
    });

    return () => {
      socket.off("new_message");
      socket.off("status_updated");
      socket.off("user_typing");
    };
  }, [selectedChat?.wa_id]);

  const fetchMessages = async () => {
    try {
      const res = await axios.get(`${API_URL}/webhook/messages`);
      if (res.data.length === 0) {
        // ðŸš€ Auto-add dummy message so UI isn't blank
        const payload = {
          type: "message",
          wa_id: "demo_wa_id",
          name: "Demo User",
          number: "+1000000000",
          message: "Welcome! This is a demo chat.",
          timestamp: new Date().toISOString(),
          meta_msg_id: "demo_msg_" + Date.now(),
          direction: "incoming",
        };
        await axios.post(`${API_URL}/webhook/receive`, payload);
        const updated = await axios.get(`${API_URL}/webhook/messages`);
        setChats(updated.data);
      } else {
        setChats(res.data);
      }
    } catch (err) {
      console.error(err);
    }
  };

  const handleSend = async () => {
    if (!newMessage || !selectedChat) return;

    const payload = {
      type: "message",
      wa_id: selectedChat.wa_id,
      name: selectedChat.name,
      number: selectedChat.number,
      message: newMessage,
      timestamp: new Date().toISOString(),
      meta_msg_id: "msg_" + Date.now(),
      direction: "outgoing",
    };

    try {
      await axios.post(`${API_URL}/webhook/receive`, payload);
      setNewMessage("");
    } catch (err) {
      console.error("Send failed:", err);
    }
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        {chats.map((chat, i) => (
          <div
            key={i}
            className={`chat-item ${
              selectedChat?.wa_id === chat.wa_id ? "selected" : ""
            }`}
            onClick={() => setSelectedChat(chat)}
          >
            <img
              src="/images/profile.png"
              alt="Profile"
              className="profile-pic"
            />
            <div>
              <strong>{chat.name}</strong>
              <br />
              <small>{chat.number}</small>
            </div>
          </div>
        ))}
      </div>

      <div className="chat-window">
        {selectedChat ? (
          <>
            <div className="chat-header">
              <strong>{selectedChat.name}</strong>
              <br />
              <small>{selectedChat.number}</small>
            </div>
            <div className="chat-messages">
              <ChatWindow chat={selectedChat} typing={typing} />
            </div>
            <div className="send-box">
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type your message..."
              />
              <button onClick={handleSend}>Send</button>
            </div>
          </>
        ) : (
          <div
            className="chat-messages"
            style={{ padding: "20px", textAlign: "center" }}
          >
            <p>Select a chat</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
